import Konva from "konva";
import { Bounds } from "./Bounds";
import { Editor } from "./Editor";
import { ElementBuilder } from "./ElementBuilder";

const design = { width: 420, height: 420 };

const presavedJson = localStorage.getItem('konva-json');
const stage = presavedJson 
  ? Konva.Node.create(presavedJson, 'container')
  : new Konva.Stage({
  container: "container",
  width: design.width,
  height: design.height
});

const bounds = new Bounds(stage);
const editor = new Editor(stage, bounds);
const elementBuilder = new ElementBuilder(editor);


if (!presavedJson) {
  elementBuilder.addRect({
    x: design.width / 2 - 20,
    y: design.height / 2 - 20,
    width: 40,
    height: 40,
    fill: "green",
    stroke: "black",
    strokeWidth: 2,
    draggable: true,
    innerBounds: true
  })

  elementBuilder.addRect({
    x: 100,
    y: 100,
    width: 40,
    height: 40,
    fill: "red",
    stroke: "black",
    strokeWidth: 2,
    draggable: true,
    outerBounds: true
  })

  elementBuilder.addText({
    x: 200,
    y: 50,
    text: 'This is my text',
    fontSize: 30,
    fontFamily: 'Roboto',
    draggable: true,
    innerBounds: true
  })

  localStorage.setItem('konva-json', stage.toJSON());
}

function setupControls() {
  const toggleBounds = document.getElementById('toggle-bounds');
  if(toggleBounds) { 
    toggleBounds.onclick = function() {
      editor.toggleBoundingLayer();
    }
  }

  const spawnImage = document.getElementById('spawn-image');
  if(spawnImage) { 
    spawnImage.onclick = function() {
      elementBuilder.addImage({
        src: 'data:image/jpeg;base64,',
        x: 120,
        y: 120,
        width: 110,
        height: 120,
        draggable: true
      })
    }
  }

  const save = document.getElementById('save-canvas');
  if(save) { 
    save.onclick = function() {
      localStorage.setItem('konva-json', stage.toJSON());
    }
  }
}

setupControls();
